import React, {Component} from 'react';
import { Link } from 'react-router-dom';

class signUpForm extends Component {
    constructor(props){
        super(props);

        this.state ={
            fields:'',
            error:''

        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleValidation(){
        let fields = this.state.fields;
        let error = {};
        let formIsValid = true;

        //Validating Name
        if(!fields["firstname"]){
            formIsValid = false;
            error["firstname"] ="Cannot be empty";
        }

        if(typeof fields["firstname"] !== "undefined"){
           if(!fields["firstname"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["firstname"] = "Only letters";
           }        
        }

        //Validating Lastname
        if(!fields["lastname"]){
            formIsValid = false;
            error["lastname"] ="Cannot be empty";
        }

        if(typeof fields["lastname"] !== "undefined"){
           if(!fields["lastname"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["lastname"] = "Only letters";
           }        
        }

        //Validating Email
        if(!fields["email"]){
           formIsValid = false;
           errors["email"] = "Cannot be empty";
        }

        if(typeof fields["email"] !== "undefined"){
           let lastAtPos = fields["email"].lastIndexOf('@');
           let lastDotPos = fields["email"].lastIndexOf('.');

           if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') == -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
              formIsValid = false;
              errors["email"] = "Email is not valid";
            }
       }  
        this.setState({errors: errors});
       return formIsValid; 
    }

   contactSubmit(e){
        e.preventDefault();

        if(this.handleValidation()){
           alert("Form submitted");
        }else{
           alert("Form has errors.")
        }

    }

    handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
    }


render() {
        return (
         <div className="FormCenter">
        <form className="FormFields" onSubmit={this.handleSubmit}>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="firstname">ชื่อจริง*</label>
        <input require type="text" id="name" className="FormField__Input" placeholder="กรุณา กรอกชื่อจริงในช่องนี้" name="firstname" onChange={this.handleChange.bind(this, "firstname")} value={this.state.fields["firstname"]}/>
        </div>
         <div className="FormField">
        <label className="FormField__Label" htmlFor="lastname">นามสกุล*</label>
        <input require type="text" id="lastname" className="FormField__Input" placeholder="กรุณา กรอกนามสกุลในช่องนี้" name="lastname" onChange={this.handleChange.bind(this, "lastname")} value={this.state.fields["lastname"]}/>
        </div>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="companyname">ชื่อบริษัท</label>
        <input type="text" id="companyname" className="FormField__Input" placeholder="กรุณา กรอกชื่อบริษัทในช่องนี้" name="companyname" value={this.state.companyname} onChange={this.handleChange}/>
        </div>
         <div className="FormField">
        <label className="FormField__Label" htmlFor="email">อีเมล์*</label>
        <input type="text" id="email" className="FormField__Input" placeholder="กรุณา กรอกอีเมล์ในช่องนี้ (e.g. johndoe@example.com)" name="email" onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]}/>
        </div>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="phonenumber">เบอร์โทรศัพท์*</label>
        <input require type="text" id="phonenumber" className="FormField__Input" placeholder="กรุณา กรอกเบอร์โทรติดต่อในช่องนี้" name="phonenumber"/>
        </div>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="numberOfEmployees">จํานวนบุคลากร*</label>
        <select require className="FormField__DropDownList" id="numberOfEmployees" name='numberOfEmployees' value={this.state.numberOfEmployees} onChange={this.handleChange}>
            <option hidden value="">เลือกจำนวนบุคลากร</option>
            <option value="less than 50">น้อยกว่า 50 คน</option>
            <option value="between 51 and 150">51 - 150 คน</option>
            <option value="more than 150">มากกว่า 150</option>
        </select>
        <div className="FormFields">
        <button className="FormField__Button mr-20">ลงทะเบียน</button>
        </div>
        </div>
        </form>
        </div>
        );
}
}


export default signUpForm;
