import React, { Component } from 'react';
import './App.css';

class App extends Component {

    constructor(){
        super();

        this.state ={
            firstname:'',
            lastname:'',
            companyname:'',
            email:'',
            phonenumber:'',
            numberOfEmployees:''

        };

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        let target = e.target;
        let value = target.type === 'checkbox' ? target.checked : target.value;
        let name = target.name;

        this.setState({
          [name]: value
        });
    }
  render() {
    return (
      <div className="App">
        <div className="App__Aside">
        <h1 className="HeaderTitle">Want to Try Techcons Biz Software?</h1>
        <p className="HeaderParagraph">An IT Solution Consulting and Services Company.</p>
        </div>
        <div className="App__Form">

        <div className="FormTitle">
        <a href="#" className="FormTitle__Link FormTitle__Link--Active">ลงทะเบียน</a>
        </div>

        <div className="FormCenter">
        <form className="FormFields" onSubmit={this.handleSubmit}>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="firstname">ชื่อจริง*</label>
        <input require type="text" id="name" className="FormField__Input" placeholder="กรุณา กรอกชื่อจริงในช่องนี้" name="name"/>
        </div>
         <div className="FormField">
        <label className="FormField__Label" htmlFor="lastname">นามสกุล*</label>
        <input require type="text" id="lastname" className="FormField__Input" placeholder="กรุณา กรอกนามสกุลในช่องนี้" name="name"/>
        </div>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="companyname">ชื่อบริษัท</label>
        <input type="text" id="companyname" className="FormField__Input" placeholder="กรุณา กรอกชื่อบริษัทในช่องนี้" name="name"/>
        </div>
         <div className="FormField">
        <label className="FormField__Label" htmlFor="email">อีเมล์*</label>
        <input require type="text" id="email" className="FormField__Input" placeholder="กรุณา กรอกอีเมล์ในช่องนี้ (e.g. johndoe@example.com)" name="email" value={this.state.email} onChange={this.handleChange}/>
        </div>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="phonenumber">เบอร์โทรศัพท์*</label>
        <input require type="text" id="phonenumber" className="FormField__Input" placeholder="กรุณา กรอกเบอร์โทรติดต่อในช่องนี้" name="phonenumber" value={this.state.phonenumber} onChange={this.handleChange}/>
        </div>
        <div className="FormField">
        <label className="FormField__Label" htmlFor="numberOfEmployees">จํานวนบุคลากร*</label>
        <select require className="FormField__DropDownList" id="numberofemployees">
            <option hidden value="">เลือกจำนวนบุคลากร</option>
            <option value="less than 50">น้อยกว่า 50 คน</option>
            <option value="between 51 and 150">51 - 150 คน</option>
            <option value="more than 150">มากกว่า 150</option>
        </select>
        <div className="FormFields">
        <button className="FormField__Button mr-20">ลงทะเบียน</button>
        </div>
        </div>
        </form>
        </div>


 </div> 
      </div>
    );
  }
}

export default App;
